import{_ as l,o as i,c as a,a5 as e}from"./chunks/framework.Cjg1mlEg.js";const p=JSON.parse('{"title":"现有功能交互规范","description":"","frontmatter":{},"headers":[],"relativePath":"docs/notes/UXGuide.md","filePath":"docs/notes/UXGuide.md","lastUpdated":1722746636000}'),t={name:"docs/notes/UXGuide.md"},r=e('<h1 id="现有功能交互规范" tabindex="-1">现有功能交互规范 <a class="header-anchor" href="#现有功能交互规范" aria-label="Permalink to &quot;现有功能交互规范&quot;">​</a></h1><p>根据现有功能，对产品交互规范进行补充，方便开发需求时，自查交互细节</p><h2 id="弹窗" tabindex="-1">弹窗 <a class="header-anchor" href="#弹窗" aria-label="Permalink to &quot;弹窗&quot;">​</a></h2><h3 id="分类" tabindex="-1">分类 <a class="header-anchor" href="#分类" aria-label="Permalink to &quot;分类&quot;">​</a></h3><ul><li>提示弹窗</li><li>确认弹窗</li><li>操作弹窗</li></ul><h3 id="提示弹窗" tabindex="-1">提示弹窗 <a class="header-anchor" href="#提示弹窗" aria-label="Permalink to &quot;提示弹窗&quot;">​</a></h3><ul><li>按钮表现： <ul><li>确认按钮：主题色</li><li>取消按钮：白色</li><li>关闭按钮：可点击</li></ul></li></ul><h3 id="确认弹窗" tabindex="-1">确认弹窗 <a class="header-anchor" href="#确认弹窗" aria-label="Permalink to &quot;确认弹窗&quot;">​</a></h3><h4 id="修改确认弹窗" tabindex="-1">修改确认弹窗 <a class="header-anchor" href="#修改确认弹窗" aria-label="Permalink to &quot;修改确认弹窗&quot;">​</a></h4><ul><li><p>文案：默认为【您的修改尚未保存，确定要离开吗?】，可根据实际情况定制</p></li><li><p>按钮表现：</p><ul><li>确认按钮：主题色</li><li>取消按钮：白色</li><li>关闭按钮：可点击</li></ul></li><li><p>取消/关闭时，前置界面状态不变更</p></li><li><p>确定时：继续下一个流程</p></li></ul><h3 id="操作弹窗" tabindex="-1">操作弹窗 <a class="header-anchor" href="#操作弹窗" aria-label="Permalink to &quot;操作弹窗&quot;">​</a></h3><h4 id="打开时" tabindex="-1">打开时 <a class="header-anchor" href="#打开时" aria-label="Permalink to &quot;打开时&quot;">​</a></h4><ul><li>按钮表现： <ul><li>确认按钮：置灰禁用</li><li>取消按钮：白色</li><li>关闭按钮：可点击</li></ul></li></ul><h4 id="保存时" tabindex="-1">保存时 <a class="header-anchor" href="#保存时" aria-label="Permalink to &quot;保存时&quot;">​</a></h4><ul><li>按钮表现： <ul><li>确认按钮： <ul><li>任务耗时： <ul><li>文案：保存中...</li><li>图标：loading</li><li>样式：置灰禁用</li></ul></li><li>任务不耗时： <ul><li>主题色</li></ul></li></ul></li><li>取消按钮：置灰禁用</li><li>关闭按钮：不可点击</li></ul></li></ul><h4 id="保存结束" tabindex="-1">保存结束 <a class="header-anchor" href="#保存结束" aria-label="Permalink to &quot;保存结束&quot;">​</a></h4><ul><li>关闭弹窗</li><li>信息回显： <ul><li>当修改产品、订单等列表时，若弹窗修改的内容包含列表字段，则需要回填修改内容。</li></ul></li></ul><h4 id="点击关闭-取消时" tabindex="-1">点击关闭/取消时 <a class="header-anchor" href="#点击关闭-取消时" aria-label="Permalink to &quot;点击关闭/取消时&quot;">​</a></h4><ul><li>按钮表现： <ul><li>确认按钮：主题色</li><li>取消按钮： <ul><li>样式：白色</li><li>点击：调用【修改确认弹窗】，沿用其规范</li></ul></li><li>关闭按钮： <ul><li>样式：可点击</li><li>点击：调用【修改确认弹窗】，沿用其规范</li></ul></li></ul></li><li>信息回显 <ul><li>当修改弹窗内容修改操作，可以直接生效而不需要点击保存按钮时，对应信息需要回填到前一个界面。</li></ul></li></ul>',19),o=[r];function u(h,d,n,s,c,_){return i(),a("div",{"data-pagefind-body":!0},o)}const q=l(t,[["render",u]]);export{p as __pageData,q as default};
