import{_ as l,o as i,c as a,a5 as e}from"./chunks/framework.Cjg1mlEg.js";const f=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/reading-notes/strategy-book.md","filePath":"docs/reading-notes/strategy-book.md","lastUpdated":1734972850000}'),t={name:"docs/reading-notes/strategy-book.md"},o=e('<p>人人必备的决策口袋书</p><h3 id="问题分类" tabindex="-1">问题分类 <a class="header-anchor" href="#问题分类" aria-label="Permalink to &quot;问题分类&quot;">​</a></h3><ul><li>需要被解决的</li><li>不需要被解决</li></ul><h3 id="管理决策的陷阱" tabindex="-1">管理决策的陷阱 <a class="header-anchor" href="#管理决策的陷阱" aria-label="Permalink to &quot;管理决策的陷阱&quot;">​</a></h3><ul><li>墨守成规 <ul><li>遵守旧规矩, 而不思考存在是否合理</li><li>理由: 事物处于动态发展中, 今天适用的规矩, 不代表明天适用</li></ul></li><li>机会陷阱 <ul><li>如果只有一个方案, 你就只剩下2个选择,要么支持,要么反对.而没有更好的选项</li></ul></li><li>希望在审批过程中提升决策的质量 <ul><li>方案被一层一层报上去，每年都有人签字，所以就这样墨守成规了, 但没人怀疑过方案是否合理</li></ul></li></ul><h2 id="目标" tabindex="-1">目标 <a class="header-anchor" href="#目标" aria-label="Permalink to &quot;目标&quot;">​</a></h2><h3 id="常见错误" tabindex="-1">常见错误 <a class="header-anchor" href="#常见错误" aria-label="Permalink to &quot;常见错误&quot;">​</a></h3><ul><li>目标不确定、</li><li>范围不明确，</li><li>或视角不清晰</li></ul><h2 id="可选方案" tabindex="-1">可选方案 <a class="header-anchor" href="#可选方案" aria-label="Permalink to &quot;可选方案&quot;">​</a></h2><ul><li>得出方案的方式 <ul><li>观察</li><li>思考</li><li>行动</li><li>循环反复</li></ul></li><li>激发想象力的方法 <ul><li>反溯法 <ul><li>我要完成一个目标A, 如果我在未来失败了,可能会有哪些因素导致的?</li></ul></li><li>联想法 <ul><li>参考其他领域经验, 看能否借鉴</li></ul></li></ul></li><li>方案制造阶段 <ul><li>不考虑可行性和成本</li><li>避免批评</li><li>鼓励胡思乱想</li><li>挑战权威</li><li>强调方法数量,而非方案质量</li><li>记录想法</li></ul></li></ul><h2 id="客观推理" tabindex="-1">客观推理 <a class="header-anchor" href="#客观推理" aria-label="Permalink to &quot;客观推理&quot;">​</a></h2><ul><li>每一个决定要占用资源, 有对应的后果</li></ul><h2 id="信息" tabindex="-1">信息 <a class="header-anchor" href="#信息" aria-label="Permalink to &quot;信息&quot;">​</a></h2><ul><li>信息的重点 <ul><li>信息评估 <ul><li>假设是否正确: 一边做决定, 一边收集信息</li><li>有没有靠近目标</li></ul></li></ul></li><li>未知管理 <ul><li>花费少量资源探索, 做实验</li><li>把未知转换成风险管理</li></ul></li><li>风险要可逆 <ul><li>一定要设立一系列的检查点来评估自己的决定</li><li>达到某个检查点时, 考虑继续前进or后退</li></ul></li><li>收集信息需要回答的问题 <ul><li>1.我们需要什么样的信息来帮助我们做下一个决定？</li><li>2.如何获得这些信息？</li><li>3.如何使用这些信息？</li></ul></li></ul><h2 id="人员" tabindex="-1">人员 <a class="header-anchor" href="#人员" aria-label="Permalink to &quot;人员&quot;">​</a></h2><ul><li>决策者：负责拍板</li><li>支持者：获得支持的情况下，决定才能进行</li><li>参与者：提供创意</li></ul><h2 id="总结" tabindex="-1">总结 <a class="header-anchor" href="#总结" aria-label="Permalink to &quot;总结&quot;">​</a></h2><p>1.随时观察时势，评估商业形势； 2.确定目标； 3.厘清优先级； 4.提出多项可选方案； 5.客观推理可选方案； 6.选择带来最佳后果的方案； 7.制定实施、沟通、评估的计划； 8.沟通，实施； 9.评估进展，并主动调整； 10.庆贺进步，再优化。</p>',18),r=[o];function u(d,h,n,s,c,_){return i(),a("div",{"data-pagefind-body":!0},r)}const m=l(t,[["render",u]]);export{f as __pageData,m as default};
